import React, { useState, useEffect } from 'react';

import classes from './AdministratorOrders.module.css';

import AddCircleIcon from '@material-ui/icons/AddCircle';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import AdministratorOrders from './AdministratorOrders';

const Administrator = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        db.collection('orders').onSnapshot(snapshot => {
            setOrders(snapshot.docs.map(doc => ({
                id: doc.id,
                order: doc.data()
            })));
        })
    }, []);



    return (
        <div className={classes.administrator}>
            <div className={classes.administrator__orders}>
                <h3 className={classes.orders__title}>Status narudžbi</h3>
                <p className={classes.orders__subtitle}>Pregled svih dosadasnjih narudzbi</p>
                <div className={classes.orders__controls}>
                    <div className={classes.orders__control}>
                        <button className={classes.orders__addDelivery}><AddCircleIcon />Add</button>
                        <button className={classes.orders__deleteButton}><DeleteForeverIcon /></button>
                        <button className={classes.orders__selectDelivery}>asd</button>
                    </div>
                    <div className={classes.orders__search}>
                        <div className={classes.orders__searchInput}>
                            <SearchIcon />
                        </div>
                        <InputBase
                            placeholder="Search…"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </div>
                </div>
                <div className={classes.orders__content}>
                    <table>
                        <thead>
                            <tr>
                                <th>Broj racuna</th>
                                <th>Kupac</th>
                                <th>Adresa</th>
                                <th>Telefon</th>
                                <th>Ukupan iznos</th>
                                <th>Dostavljac</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default Administrator
