import React, { useState } from 'react';
import moment from 'moment';

import './Dashboard.css';
import Logo from '../assets/infostud_logo.png';

import MenuIcon from '@material-ui/icons/Menu';

import Sidebar from '../components/Sidebar';
import EmployeesList from '../components/EmployeesList';
import UpdateWorkspace from '../components/UpdateWorkspace';

const Dashboard = () => {

    const [showNav, setShowNav] = useState(false);

    const openHandler = () => {
        setSidebarOpen(!sidebarOpen);
    }

    const sidebarCloseHandler = () => {
        setSidebarOpen(false);
    }

    let sidebar;

    if (sidebarOpen) {
        sidebar = <Sidebar sidebar={"sidebar"} close={sidebarCloseHandler} />
    }

    return (
        <div className="dashboard">
            <header>
                <MenuIcon onClick={() => setShowNav(!showNav)} />
                <img src={Logo} alt="Infostud Logo" />
                <span>{moment().format('LLL')}</span>
            </header>
            <div className="dashboard__content">
                {/* {sidebar} */}
                {showNav && <Sidebar />}
                <EmployeesList />
                <UpdateWorkspace />
            </div>
        </div>
    )
}

export default Dashboard
