import moment from 'moment';
import React, { useState } from 'react';

import './UpdateWorkspace.css';
import WorkspaceCard from './WorkspaceCard';
import Button from '@material-ui/core/Button';

const UpdateWorkspace = () => {

    const [dates, setDates] = useState([]);

    const displayCardHandler = () => {
        let count = 0;

        const today = moment().format("DD.MM.YYYY");

        for(let i = 0; i < 7; i++) {
            const tempDate = moment(today, "DD-MM-YYYY").add(i, 'days');
            const day = tempDate.format('DD');
            const month = tempDate.format('MM');
            const year = tempDate.format('YYYY');
            const dayName = tempDate.format('dddd');
            const finalDate = day + '.' + month + '.' + year;
            // setDates(prevState => {
            //     let tmpArr = [prevState];
            //     tmpArr.push(finalDate);
            //     return tmpArr;
            // });

            //setDates(dates.concat(finalDate));

            if(dayName === "Saturday" || dayName === "Sunday") {
                console.log(finalDate, dayName);
            } else {
                continue;
            }

            
        }
        

        
    }

    const [workplace, setWorkplace] = useState('');

    const updateWorkplaceHandler = (event) => {
        event.preventDefault();
        setWorkplace(event.target.value);
    }

   

    // if (currentDate === 'Saturday' || currentDate === 'Sunday') {
    //     console.log('Danas je vikend');
    // } else {
    //     console.log('Danas nije vikend');
    // }
    

    return (
        <div className="updateWorkspace">
            <h1></h1>
            <h4>Promenite radno mesto za narednih 7 dana</h4>
            <div className="updateWorkspace__wrapper">
                {displayCardHandler()}
                <div className="updateWorkspace__card">
                <span>25.05.2021.</span>
                <select value={workplace} onChange={updateWorkplaceHandler}>
                    <option value="Od kuce">Od kuce</option>
                    <option value="Iz kancelarije">Iz kancelarije</option>
                    <option value="Odsutan">Odsutan</option>
                </select>
                {workplace === 'Odsutan' ? <textarea placeholder="Obrazlozite razlog odsustva" /> : null}
                <Button>Sacuvaj</Button>
            </div> 
            <div className="updateWorkspace__card">
                <span>25.05.2021.</span>
                <select value={workplace} onChange={updateWorkplaceHandler}>
                    <option value="Od kuce">Od kuce</option>
                    <option value="Iz kancelarije">Iz kancelarije</option>
                    <option value="Odsutan">Odsutan</option>
                </select>
                {workplace === 'Odsutan' ? <textarea placeholder="Obrazlozite razlog odsustva" /> : null}
                <Button>Sacuvaj</Button>
            </div> 
            <div className="updateWorkspace__card">
                <span>25.05.2021.</span>
                <select value={workplace} onChange={updateWorkplaceHandler}>
                    <option value="Od kuce">Od kuce</option>
                    <option value="Iz kancelarije">Iz kancelarije</option>
                    <option value="Odsutan">Odsutan</option>
                </select>
                {workplace === 'Odsutan' ? <textarea placeholder="Obrazlozite razlog odsustva" /> : null}
                <Button>Sacuvaj</Button>
            </div> 
            </div>
        </div>
    )
}

export default UpdateWorkspace
