import React, { useState } from 'react';

import './Employee.css';

import Logo from '../assets/users.png';

import { Avatar } from '@material-ui/core';
import CommentOutlinedIcon from '@material-ui/icons/CommentOutlined';

const Employee = () => {

    const slobodan = "Odsutan";
    const [comment, setComment] = useState(false);

    return (
        <div className="employee">
            <Avatar src={Logo}  />
            <span>Adam Horvat</span>
            <span>Infostud 3</span>
            <span>Subotica</span>
            <span>Polovni automobili</span>
            <span>Frontend Developer</span>
            <span>24.05.2021.</span>
            {/* <select name="workspace">
                <option value="volvo">Nije izabrano</option>
                <option value="saab">Od kuce</option>
                <option value="mercedes">Iz kancelarije</option>
                <option value="audi">Odsutan</option>
            </select> */}
            <span>Rad od kuce</span>
            {slobodan==="Odsutan" ? 
                <CommentOutlinedIcon onMouseEnter={() => setComment(true)} onMouseLeave={() => setComment(false)} /> : 
                ""}

            {comment && <div className="poruka">
                <p>Odsutan zbog godisnjeg odmora</p>
            </div>}
        </div>
    )
}

export default Employee
